import React from 'react';
import R from 'ramda';
import { connect } from 'react-redux';
import { getCategories, getActiveCategoryId } from 'selectors';
import { Link, withRouter } from 'react-router';
import {compose} from 'redux';
import classNames from 'classnames';

const Categories = ({categories, activeCategoryId}) => {
	console.log('active category id', activeCategoryId);
	const getActiveState =  R.propEq('id', activeCategoryId);
	const renderCategory = (category, index) => {
		console.log('category', `${category.id} olo ${activeCategoryId}`);
		const linkClass = classNames({
			'list-group-item': true,
			'active': getActiveState(category)
		});
		return (
			<Link
				to={`/categories/${category.id}`}
				className={linkClass}
				key={index}
			>
				{category.name}
			</Link>
		)
	};
	const renderAllCategory = () => {
		const linkClass = classNames({
			'list-group-item': true,
			'active': R.isNil(activeCategoryId)
		});
		return (
			<Link
				to='/'
				className={linkClass}
			>
				All
			</Link>
		)
	}
	return (
		<div className="well">
			<h4>Brand</h4>
			<div className="list-group">
				{renderAllCategory()}
				{categories && categories.map((category, index) => renderCategory(category, index))}
			</div>
		</div>
	)
};
const mapStateToProps = (state, ownProps) => ({
	categories: getCategories(state),
	activeCategoryId: getActiveCategoryId(ownProps)
});
export default compose(
	withRouter,
	connect(mapStateToProps, null)
)(Categories)
